import { Drawer, Input, Modal } from "antd";
import styles from "../styles.module.sass";
import { FC } from "react";
import { useMediaQuery } from "react-responsive";

interface NoDQModalProps {
  noDQModalState: boolean;
  showNoDQModal: () => void;
  OnDQModalCancel: () => void;
  setInterestTextAvailability: (value: string) => void;
  interestTextAvailability: string;
  onRequest: () => void;
}

const NoDQModal: FC<NoDQModalProps> = ({
  noDQModalState,
  showNoDQModal,
  OnDQModalCancel,
  interestTextAvailability,
  setInterestTextAvailability,
  onRequest,
}) => {
  const isMobile = useMediaQuery({ maxWidth: 610 });
  return (
    <>
      {isMobile ? (
        <Drawer
          style={{ borderTopLeftRadius: "5%", borderTopRightRadius: "5%" }}
          placement={"bottom"}
          closeIcon={false}
          push={false}
        >
          <section className={styles.modalContainer}>
            <div className={styles.modalTitleContainer}>
            <h3>Availability to Meet</h3>
              <button onClick={onRequest} className={styles.declineSubBtn}>
                Submit
              </button>
              <button onClick={OnDQModalCancel} className={styles.cancelBtn}>
                Cancel
              </button>
            </div>
            <small>Share your upcoming availability below or just click Submit.</small>
            <div>
              <div className={styles.checkboxContainer}>
                <Input
                  bordered={false}
                  placeholder={"Enter availability here"}
                  value={interestTextAvailability}
                  onChange={(e) => setInterestTextAvailability(e.target.value)}
                />
              </div>
            </div>
          </section>
        </Drawer>
      ) : (
        <Modal
          maskClosable={false}
          centered={true}
          width={"665px"}
          footer={null}
          closeIcon={false}
          open={noDQModalState}
        >
          <section className={styles.modalContainer}><div>
                  <h4>Share your upcoming availability below or just click Submit:</h4> </div>
                    <div className={styles.checkboxContainer}>
                      <Input
                        bordered={false}
                        placeholder={"Enter here"}
                        value={interestTextAvailability}
                        onChange={(e) =>
                          setInterestTextAvailability(e.target.value)
                        }
                      />
                    </div></section>
                    <div>&nbsp;</div><section className={styles.modalContainer}><div className={styles.modalTitleContainer}>

              <div className={styles.modalButtonsContainer}>
                <button onClick={onRequest} className={styles.declineSubBtn}>
                  Submit
                </button>
                <button onClick={OnDQModalCancel} className={styles.cancelBtn}>
                  Cancel
                </button>
              </div>
            </div>
          </section>
        </Modal>
      )}
    </>
  );
};

export default NoDQModal;
